(1, 20151203, 1, '', '', 'Indicaciones para revisor(a)', 'Estimado $name,&#13;\n&#13;\nRecientemente se presentó una nueva propuesta al comité y usted ha sido seleccionado como evaluador de este proyecto titulado &quot;$title&quot;.&#13;\n&#13;\nProtocol: &lt;B&gt;$protocol&lt;/B&gt;&#13;\n&#13;\nPara tener acceso a este proyecto, por favor ingrese al sistema. Si tiene cualquier pregunta o duda, contáctenos a XXX@XXX&#13;\n&#13;\nSaludos cordiales,&#13;\n&#13;\n&lt;B&gt;Comité de ética&lt;/B&gt;&#13;\n&#13;\n$link', 19000101, 19000101, '', 1, 'indicate_email', '', 'es', 0, 0),
(2, 20150820, 1, '', '', 'Senha info', 'Prezado Investigador,&#13;\n&#13;\nConforme sua solicitação foi encaminho um e-mail com sua senha.', 19000101, 19000101, '', 1, 'send_email_text', '', 'pt_BR', 0, 0),
(3, 20150820, 1, '', '', 'End of review process', 'Dear Investigador,&#13;\n&#13;\nThe review process of the protocol you submitted to this committee has finalized.&#13;\n&#13;\nPlease log into the system to access the official decision.&#13;\n&#13;\nRegards,&#13;\n&#13;\nEthics Committee', 19000101, 19000101, '', 1, 'communicate_investig', '', 'en_US', 0, 0),
(4, 20150820, 1, '', '', 'Your password information', 'Dear User,&#13;\n&#13;\nYour password has been sent to your e-mail address on file.&#13;\n&#13;\nRegards,', 19000101, 19000101, '', 1, 'send_email_content', '', 'es', 0, 0),
(5, 20151203, 1, '', '', 'Forgotten password information', 'Dear Investigator,&#13;\n&#13;\nAs requested, an email has been sent to you with your password information.&#13;\n&#13;\nRegards, best wishes!&#13;\n&#13;\nEthics Committee', 19000101, 19000101, '', 1, 'send_email_text', '', 'en_US', 0, 0),
(6, 20150611, 1, '', '', '¿Olvidó su contraseña?', 'Estimado investigador,&#13;\n&#13;\nTal como lo solicitó, se le ha enviado un email con la información de su contraseña.&#13;\n&#13;\nSaludos,&#13;\n&#13;\nComité de Ética', 19000101, 19000101, '', 1, 'send_email_text', '', 'es', 0, 0),
(7, 20150611, 1, '', '', 'Instructions for reviewers', 'Dear $name,&#13;\n&#13;\nA new proposal was recently submitted to this committee and you have been selected as evaluator of the project titled. &quot;$title&quot;.&#13;\n&#13;\nProtocol: &lt;B&gt;$protocol&lt;/B&gt;&#13;\n&#13;\nTo access this project, please log into the system. If you have any questions or comments, please contact us at pahoerc@paho.org &#13;\n&#13;\nKind regards,&#13;\n&#13;\n&lt;B&gt;Ethics Committee&lt;/B&gt;&#13;\n&#13;\n$link', 19000101, 19000101, '', 1, 'indicate_email', '', 'en_US', 0, 0),
(8, 20150820, 1, '', '', 'Finalización del proceso de revisión', 'Estimado investigador,&#13;\n&#13;\nEl proceso de revisión del protocolo presentado a este comité ha finalizado.&#13;\n&#13;\nPor favor entre al sistema para acceder la decisión oficial.&#13;\n&#13;\nSaludos,&#13;\n&#13;\nComité de Ética', 19000101, 19000101, '', 1, 'communicate_investig', '', 'es', 0, 0),
(9, 20150820, 1, '', '', 'Protocol returned for corrections', 'Dear Investigator,&#13;\n&#13;\nYour proposal presents certain issues that we need you to correct. Please revise and resubmit when corrected.&#13;\n&#13;\nKind regards,&#13;\n&#13;\nEthics Committee', 19000101, 19000101, '', 1, 'communicate_return', '', 'en_US', 0, 0),
(10, 20150316, 1, '', '', 'Confirmación de creación de usuario', 'Estimado $name,&#13;\n($email)&#13;\n&#13;\nMuchas gracias por la creación de su usuario. Haga clic acá: $link&#13;\n&#13;\nHasta luego,&#13;\n&#13;\nComité de Ética', 19000101, 19000101, '', 1, 'email_confirm_email', '', 'es', 0, 0),
(11, 20151014, 1, '', '', 'Protocolo asignado para su revisión', 'Estimado revisor,&#13;\n&#13;\nEl protocolo en mención le ha sido asignado para su revisión.&#13;\n&#13;\nPor favor ingrese al sistema ProEthos para acceder a este protocolo.&#13;\n&#13;\nSi tiene cualquier duda, por favor no dude en comunicarse con nosotros.&#13;\n&#13;\nSaludos&#13;\n&#13;\nComité de Ética', 19000101, 19000101, '', 1, 'survey_email', '', 'es', 0, 0),
(12, 20151014, 1, '', '', 'Protocol assigned for your review', 'Dear reviewer,&#13;\n&#13;\nThe protocol here mentioned has been assigned to you for review.&#13;\n&#13;\nPlease go to the ProEthos system in order to access this protocol.&#13;\n&#13;\nIf you have any questions, please don/t hesitate to contact us.&#13;\n&#13;\nRegards&#13;\n&#13;\nEthics Committee', 19000101, 19000101, '', 1, 'survey_email', '', 'en_US', 0, 0),
(13, 20150611, 1, '', '', 'Senha info', 'Estimado Investigador&#13;\n&#13;\nConforme a lo solicitado, se le esta enviando un correo con su contraseña.&#13;\n&#13;\nSaludos', 19000101, 19000101, '', 1, 'send_email_tex', '', 'es', 0, 0),
(14, 20151203, 1, '', '', 'Su protocolo ha sido presentado exitosamente', '$INFORMACION_DEL_PROTOCOLO&#13;\n&#13;\nEstimado investigador,&#13;\n&#13;\nEl protocolo arriba mencionado ha sido presentado a este comité exitosamente.&#13;\nLo mantendremos informado del avance del proceso de revisión ética de su proyecto.&#13;\n&#13;\nSaludos,&#13;\n&#13;\n$INFORMACION_DEL_COMITE', 19000101, 19000101, '', 1, 'email_confirm_subm', '', 'es', 0, 0),
(15, 20150819, 1, '', '', 'Hay un nuevo protocolo presentado para revisión', '$INFORMACION_DEL_PROTOCOLO&#13;\n&#13;\nUn nuevo protocolo ha sido presentado para revisión.&#13;\n&#13;\nPara mayor información, por favor ingrese al sistema.&#13;\n&#13;\nProEthos&#13;\n', 19000101, 19000101, '', 1, 'email_notify_subm', '', 'es', 0, 0),
(16, 20151014, 1, '', '', 'La acción de seguimiento de su protocolo ha sido recibida', 'Estimado investigador,&#13;\n&#13;\nHemos recibido la acción de seguimiento de su protocolo.&#13;\n&#13;\nLe mantendremos informando del avance de este proceso.&#13;\n&#13;\nSaludos,&#13;\n&#13;\nComité de Ética', 19000101, 19000101, '', 1, 'email_monitoreo_subm', '', 'es', 0, 0);
